package main

import "fmt"

func longestPalindrome(s string) string {
	if len(s) == 0 {
		return ""
	}

	longest := s[0:1]
	for i := 0; i < len(s); i++ {
		t_longest := ""

		for s1, s2 := i, i+1; s1 >= 0 && s2 < len(s); s1, s2 = s1-1, s2+1 {
			if s[s1] == s[s2] {
				t_longest = s[s1 : s2+1]
			} else {
				break
			}
		}

		if len(t_longest) > len(longest) {
			longest = t_longest
		}

		t_longest = ""

		for s1, s2 := i-1, i+1; s1 >= 0 && s2 < len(s); s1, s2 = s1-1, s2+1 {
			if s[s1] == s[s2] {
				t_longest = s[s1 : s2+1]
			} else {
				break
			}
		}

		if len(t_longest) > len(longest) {
			longest = t_longest
		}
	}

	return longest
}

func main() {
	fmt.Println(longestPalindrome("abcba"))
	fmt.Println(longestPalindrome("caba"))
	fmt.Println(longestPalindrome(""))
	fmt.Println(longestPalindrome(""))
	fmt.Println(longestPalindrome("a"))
	fmt.Println(longestPalindrome("babad"))
	fmt.Println(longestPalindrome("cbbd"))
}
